data.userrole:
// **** User Role Data ****

INSERT INTO UserRole (RoleID, UserID)
values ((SELECT RoleID FROM Role WHERE RoleName = 'user'), (SELECT UserID FROM User WHERE UserName = 'test1' AND UserPassword = 'test2'));

INSERT INTO UserRole (RoleID, UserID)
values ((SELECT RoleID FROM Role WHERE RoleName = 'admin'), (SELECT UserID FROM User WHERE UserName = 'admin' AND UserPassword = 'admin'));

data.configoptions:
